__author__ = 'Dex Devlon'
__doc__ = 'binarystring is a python package to convert binary to string and vice-versa'

from binascii import a2b_base64, b2a_base64

class error(Exception): # for error handling
    pass

class Binary2String:

    def __init__(self, binary):
        self.binary = binary

    def b2s(self):
        self.string = "".join(chr(int(e)) for e in self.binary)
        return self.string

    def b2s_base64(self):
        data_b2s = b2a_base64(self.binary)
        return data_b2s.decode()

class String2Binary:

    def __init__(self, string):
        self.string = string

    def s2b(self):
        self.binary = bytes(ord(e) for e in self.string)
        return self.binary

    def s2b_base64(self):
        data_b2s = a2b_base64(self.string)
        return data_b2s

def b2s(binary: bytes):
    return Binary2String(binary).b2s()

def b2s_base64(binary: bytes):
    return Binary2String(binary).b2s_base64()

def s2b(string: str):
    return String2Binary(string).s2b()

def s2b_base64(binary: bytes):
    return String2Binary(binary).s2b_base64()
